# Function: Install a single package
function Install-Package {
    param (
        [string]$PackageId,
        [string]$PackageName
    )
    Write-Host "Installing $PackageName ..." -ForegroundColor Cyan
    try {
        # -e: accept license agreements, -h: hide progress UI, -s: silent (if supported)
        winget install --id $PackageId --silent --accept-source-agreements --accept-package-agreements -h
        if ($LASTEXITCODE -eq 0) {
            Write-Host "✅ $PackageName installed successfully." -ForegroundColor Green
        } else {
            Write-Host "⚠️ $PackageName may have failed (exit code $LASTEXITCODE)." -ForegroundColor Yellow
        }
    } catch {
        Write-Host "❌ Error installing $PackageName" -ForegroundColor Red
    }
}

